angular.module('App')
.controller('PatientDetailsController', function ($scope, $http, $ionicLoading, AppService) {
  $ionicLoading.show();
  $http({
			method : 'GET',
			url : AppService.baseURL + '/fhir/Patient?identifier=http://fhir.nhs.net/Id/nhs-number|'+AppService.NHSNumber+'&_format=json',
			headers: {
			   'Authorization' : 'Bearer eyJhbGciOiJub25lIiwidHlwIjoiSldUIn0..',
                            'Ssp-From' : '200000000359',
                            'Ssp-InteractionID':'urn:nhs:names:services:gpconnect:fhir:rest:search:patient',
                            'Ssp-To':'200000000360',
                            'Ssp-TraceID':'45a2728a-bafe-ffb9-c898-a456128c507a'
        }
    })
        .success(function (patientdata) {
    $scope.patient = patientdata.entry[0].resource;
    $ionicLoading.hide();
  }).error(function (err) {
    $ionicLoading.show({
      template: 'Could not load patient details. Please try again later.',
      duration: 3000
    });
  })
});

